package lecture1;

public class swap2num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//swap using 3rd variable ie temp variable
		
		int first = 23;
		int sec =   34;
		int temp;
		System.out.println("thefirst no.is"+first);
		System.out.println("the second no.is"+sec);
		
		temp = first;
		first = sec;
		sec = temp;
	    System.out.println("now after swapping the first no. is "+first);
	    System.out.println("now after swapping the second no. is "+sec);


	}

	}

             

